import React, { useState, useEffect } from 'react';
import Navigation from '../Navigation/NavigationPage';
import Footer from '../Footer/FooterPage';
import { Table, Button } from 'react-bootstrap'; // Importing Bootstrap components

const Worker = () => {
  // Sample data fetched from work collection (Replace with actual data fetching)
  const [workData, setWorkData] = useState([]);

  useEffect(() => {
    // Replace with actual data fetching logic
    const fetchData = async () => {
      const response = await fetch('/api/work'); // Replace with your API endpoint
      const data = await response.json();
      setWorkData(data);
    };

    fetchData();
  }, []);

  return (
    <div>
      <Navigation />
      <div className="container mt-4">
        <h1>Worker Page</h1>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>SNO</th>
              <th>Work Title</th>
              <th>Salary</th>
              <th>Hours</th>
              <th>Description</th>
              <th>Username</th>
              <th>Farm Name</th>
              <th>Farm Location</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {workData.map((work, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{work.workTitle}</td>
                <td>{work.salary}</td>
                <td>{work.hours}</td>
                <td>{work.description}</td>
                <td>{work.username}</td>
                <td>{work.farmName}</td>
                <td>{work.farmerAddress.farmLocation}</td>
                <td>
                  <Button variant="primary" onClick={() => handleApply(work)}>Apply</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
      <Footer />
    </div>
  );

  function handleApply(work) {
    // Handle the apply action here
    alert(`Applied for ${work.workTitle}`);
  }
};

export default Worker;
