import React, { useState } from 'react';
import './Checkout.css'; // Import CSS file

const CheckoutView = ({ cart }) => {
  const [cartItems, setCartItems] = useState(cart.map(item => ({ ...item, quantity: 1 })));
  const [paymentMethod, setPaymentMethod] = useState('');
  const [order, setOrder] = useState(null);
  const [shippingAddress, setShippingAddress] = useState('');
  const [error, setError] = useState('');

  // Function to handle quantity change
  const handleQuantityChange = (index, newQuantity) => {
    const updatedCart = [...cartItems];
    updatedCart[index].quantity = newQuantity;
    setCartItems(updatedCart);
  };

  // Function to calculate total price
  const calculateTotal = () => {
    let total = 0;
    cartItems.forEach(item => {
      total += item.price * item.quantity;
    });
    return total;
  };

  // Function to handle payment method selection
  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  // Function to generate an order number
  const generateOrderNumber = () => {
    const now = new Date();
    const date = now.toISOString().slice(0, 10).replace(/-/g, '');
    const sequenceNumber = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
    return `${date}${sequenceNumber}`;
  };

  // Function to handle payment and order generation
  const handlePayment = () => {
    if (!paymentMethod || !shippingAddress) {
      setError('Please select a payment method and provide a shipping address.');
      return;
    }

    const orderNumber = generateOrderNumber();
    const orderDetails = {
      orderNumber,
      shippingAddress,
      cartItems,
      total: calculateTotal(),
      paymentMethod,
    };

    // Set the order details for display
    setOrder(orderDetails);

    // Send order details to backend (Example API call)
    fetch('http://localhost:3000/orders', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        orderNumber,
        shippingAddress,
        cartItems,
        total: calculateTotal(),
        paymentMethod,
      }),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Order placed successfully:', data);
      })
      .catch(error => {
        console.error('Error placing order:', error);
        setError('Error placing order');
      });
  };

  return (
    <div className="Checkout">
      <h2 className="head">Checkout</h2>
      {error && <p className="error-message">{error}</p>}
      {order ? (
        <div className="order-summary">
          <h3>Order Summary</h3>
          <p><strong>Order Number:</strong> {order.orderNumber}</p>
          <p><strong>Shipping Address:</strong> {order.shippingAddress}</p>
          <h4>Checkout Details</h4>
          <table>
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
              </tr>
            </thead>
            <tbody>
              {order.cartItems.map((item, index) => (
                <tr key={index}>
                  <td>{item.productName}</td>
                  <td>${item.price}</td>
                  <td>{item.quantity}</td>
                  <td>${(item.price * item.quantity).toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3>Total: ${order.total.toFixed(2)}</h3>
          <p>Thank you for your purchase!</p>
          <p>If you have any issues, please contact our support team.</p>
          <p>Refund Policy: If you're not satisfied with your purchase, please contact us within 30 days for a refund.</p>
        </div>
      ) : (
        <div>
          <table>
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Category</th>
                <th>Sold By</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={index}>
                  <td>{item.productName}</td>
                  <td>${item.price}</td>
                  <td>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))}
                    />
                  </td>
                  <td>${(item.price * item.quantity).toFixed(2)}</td>
                  <td>{item.category}</td>
                  <td>{item.username}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="total-section">
            <h3>Total: ${calculateTotal().toFixed(2)}</h3>
            <div className="payment-method">
              <h3>Select Payment Method</h3>
              <label>
                <input
                  type="radio"
                  name="paymentMethod"
                  value="credit"
                  checked={paymentMethod === 'credit'}
                  onChange={handlePaymentMethodChange}
                />
                Credit Card
              </label>
              <label>
                <input
                  type="radio"
                  name="paymentMethod"
                  value="debit"
                  checked={paymentMethod === 'debit'}
                  onChange={handlePaymentMethodChange}
                />
                Debit Card
              </label>
              <label>
                <input
                  type="radio"
                  name="paymentMethod"
                  value="interact"
                  checked={paymentMethod === 'interact'}
                  onChange={handlePaymentMethodChange}
                />
                Interac
              </label>
              {paymentMethod && (
                <div className="payment-details">
                  <h3>Payment Details</h3>
                  <label>
                    Shipping Address:
                    <input
                      type="text"
                      value={shippingAddress}
                      onChange={(e) => setShippingAddress(e.target.value)}
                    />
                  </label>
                  <button className="payment-button" onClick={handlePayment}>Pay</button>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CheckoutView;
