import React, { useState } from 'react';
import './Checkout.css'; // Import CSS file

const CheckoutView = ({ cart }) => {
  const [cartItems, setCartItems] = useState(cart.map(item => ({ ...item, quantity: 1 })));
  const [paymentMethod, setPaymentMethod] = useState('');
  const [paymentDetails, setPaymentDetails] = useState({});
  const [orderDetails, setOrderDetails] = useState(null);
  const [error, setError] = useState('');

  const handleQuantityChange = (index, newQuantity) => {
    const updatedCart = [...cartItems];
    updatedCart[index].quantity = newQuantity;
    setCartItems(updatedCart);
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handlePaymentDetailsChange = (e) => {
    setPaymentDetails({ ...paymentDetails, [e.target.name]: e.target.value });
  };

  const handlePayment = async () => {
    try {
      const orderNumber = generateOrderNumber();
      const shippingAddress = "Customer's Shipping Address"; // Replace with actual address input
      const total = calculateTotal();
      const response = await fetch('http://localhost:3000/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          orderNumber,
          shippingAddress,
          cartItems,
          total,
          paymentMethod,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to create order');
      }

      const order = await response.json();
      setOrderDetails(order);
    } catch (err) {
      setError('Failed to process payment');
    }
  };

  const generateOrderNumber = () => {
    const now = new Date();
    const date = `${now.getMonth() + 1}${now.getDate()}${now.getFullYear()}`;
    const sequenceNumber = Math.floor(Math.random() * 1000).toString().padStart(4, '0');
    return `${date}${sequenceNumber}`;
  };

  return (
    <div className="Checkout">
      <h2 className="head">Checkout</h2>
      {error && <p className="error-message">{error}</p>}
      {orderDetails ? (
        <div>
          <h3>Order Summary</h3>
          <p>Order Number: {orderDetails.orderNumber}</p>
          <p>Shipping Address: {orderDetails.shippingAddress}</p>
          <table>
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Category</th>
                <th>Sold By</th>
              </tr>
            </thead>
            <tbody>
              {orderDetails.cartItems.map((item, index) => (
                <tr key={index}>
                  <td>{item.productName}</td>
                  <td>${item.price}</td>
                  <td>{item.quantity}</td>
                  <td>${(item.price * item.quantity).toFixed(2)}</td>
                  <td>{item.category}</td>
                  <td>{item.username}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3>Total: ${orderDetails.total.toFixed(2)}</h3>
          <p>Thank you for your purchase!</p>
          <p>Cancel Note: If you wish to cancel, please contact us.</p>
          <p>Refund Policy: Refunds are available within 30 days of purchase.</p>
        </div>
      ) : (
        <div>
          <div className="payment-method">
            <h3>Select Payment Method</h3>
            <label>
              <input
                type="radio"
                name="paymentMethod"
                value="credit"
                checked={paymentMethod === 'credit'}
                onChange={handlePaymentMethodChange}
              />
              Credit Card
            </label>
            <label>
              <input
                type="radio"
                name="paymentMethod"
                value="debit"
                checked={paymentMethod === 'debit'}
                onChange={handlePaymentMethodChange}
              />
              Debit Card
            </label>
            <label>
              <input
                type="radio"
                name="paymentMethod"
                value="interact"
                checked={paymentMethod === 'interact'}
                onChange={handlePaymentMethodChange}
              />
              Interact
            </label>
          </div>

          {paymentMethod && (
            <div className="payment-details">
              <h3>Enter Payment Details</h3>
              <form>
                {paymentMethod === 'credit' && (
                  <div>
                    <label>Card Number:</label>
                    <input
                      type="text"
                      name="cardNumber"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                    <label>Expiry Date:</label>
                    <input
                      type="text"
                      name="expiryDate"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                    <label>CVC:</label>
                    <input
                      type="text"
                      name="cvc"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                  </div>
                )}
                {paymentMethod === 'debit' && (
                  <div>
                    <label>Card Number:</label>
                    <input
                      type="text"
                      name="cardNumber"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                    <label>Expiry Date:</label>
                    <input
                      type="text"
                      name="expiryDate"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                    <label>CVC:</label>
                    <input
                      type="text"
                      name="cvc"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                  </div>
                )}
                {paymentMethod === 'interact' && (
                  <div>
                    <label>Account Number:</label>
                    <input
                      type="text"
                      name="accountNumber"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                    <label>Routing Number:</label>
                    <input
                      type="text"
                      name="routingNumber"
                      onChange={handlePaymentDetailsChange}
                      required
                    />
                  </div>
                )}
                <button type="button" onClick={handlePayment}>Pay</button>
              </form>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CheckoutView;
